import { Component } from '@angular/core';
import { ImageService } from 'src/app/services/image.service';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

import { Observable } from 'rxjs';

@Component({
  selector: 'app-image-gallery',
  templateUrl: './image-gallery.component.html',
  styleUrls: ['./image-gallery.component.css']
})
export class ImageGalleryComponent {
  images: any[] = [];
  isLoanding: boolean = true;
  selectedImage: any = null;
  error: string | null = null;

  // configuración de la galería
  itemsPerPage: number = 10;
  currentPage: number = 1;

  constructor(private imageService: ImageService) {}

  ngOnInit(): void {
    this.loadImages();
  }
  loadImages(): void {
    this.isLoanding = true;
    this.error = null;

    this.imageService.getImages(this.itemsPerPage).subscribe({
      next: (data): void => {
        this.images = data;
        this.isLoanding = false;
      },
      error: (error): void => {
        this.error = 'Error loading images';
        this.isLoanding = false;
        console.error('Error loading images', error);
      }
    });
  }

  showImageDetails(id: string): void {
  this.imageService.getImageDetails(id).subscribe({
    next: (details): void => {
      this.selectedImage = details;
    },
    error: (err): void => {
      console.error(err);
    }
  });
}
closemodal(): void {
  this.selectedImage = null;
}

}
<div class="gallery-container">
<h2>Galeria de imagenes</h2>
<div *ngIf="isLoanding" class="loading">
  <p>Cargando imágenes...</p>
</div>

<div *ngIf="error" class="error">
  <p>{{ error }}</p>
  <button (click)="loadImages()">Reintentar</button>
</div>

<div class="gallery-grid" *ngIf="!isLoanding && !error">
  <div class="image-card" *ngFor="let image of images" (click)="showImageDetails(image.id)">
    <img [src]="'https://picsum.photos/id/' + image.id + '/300/200'" [alt]="image.author">
    <div class="image-info">
      <h3>{{ image.author }}</h3>
      <p>Ancho: {{ image.width }}px</p>
      <p>Alto: {{ image.height }}px</p>
    </div>
  </div>
</div>
